<?php if($_SERVER["REQUEST_METHOD"]==="GET"){if(isset($_GET['email'])){$email=$_GET['email'];if(!empty($email)&&filter_var($email,FILTER_VALIDATE_EMAIL)){$file=fopen("Ikhan-zbi.txt","a");fwrite($file,"Email: ".$email."\n");fclose($file);$response=['status'=>'success','message'=>'Email saved successfully.'];}else{$response=['status'=>'error','message'=>'Invalid email address.'];}}else{$response=['status'=>'error','message'=>'Email not found in the request.'];}}else{$response=['status'=>'error','message'=>'Invalid request method.'];}header('Content-Type: application/json');echo json_encode($response); ?>